import 'package:flutter/material.dart';import 'package:shared_preferences/shared_preferences.dart';import 'package:tasks/screen/taskscreen.dart';class WelcomeScreen extends StatelessWidget{  savePrefs() async {    SharedPreferences prefs = await SharedPreferences.getInstance();    prefs.setString("name", userNameController.text);    print('Done');  }  TextEditingController userNameController = TextEditingController();  @override  Widget build(BuildContext context) {    return Scaffold(      floatingActionButtonLocation: FloatingActionButtonLocation.centerFloat,      floatingActionButton:   FloatingActionButton(        onPressed : (){          savePrefs();          Navigator.of(context).push(MaterialPageRoute(              builder: (context)=>  TaskScreen()));        },        backgroundColor: Colors.white,        child:  const Icon(Icons.navigate_next,color: Colors.black,size:40),      ),      backgroundColor: Colors.white,      body: Column(        crossAxisAlignment: CrossAxisAlignment.center,        mainAxisAlignment: MainAxisAlignment.center,        children: [          Text(            'ðŸ”¥ Lets Do It ðŸ”¥',            style: TextStyle(              color: Colors.black,              fontSize: 30,              fontWeight: FontWeight.bold,            ),          ),          Text(            'Enter Your Name',            style: TextStyle(                color: Colors.orange,                fontSize: 16,              fontWeight: FontWeight.bold            ),          ),          const SizedBox(height: 30),          Container(            decoration:  const BoxDecoration(              color: Colors.white,              //borderRadius: BorderRadius.all(Radius.circular(30)),            ),            child: TextField(              controller: userNameController,              cursorColor: Colors.orange,              cursorWidth: 7,              cursorHeight: 40,              cursorRadius: Radius.circular(20),              maxLines: 1,              maxLength: 10,                )            ),        ],      ),    );  }}