import 'package:flutter/material.dart';import 'package:provider/provider.dart';import '../model/task_data.dart';class AddTaskScreen extends StatelessWidget{final Function addTaskCallback;AddTaskScreen(this.addTaskCallback);  @override  Widget build(BuildContext context) {    String? newTaskTitle ;    return Container(      padding:  const EdgeInsets.all(50),      child: Column(        crossAxisAlignment: CrossAxisAlignment.stretch,        children: [           const Text ('Add Task',          textAlign: TextAlign.center,          style: TextStyle(            fontSize: 30,            color: Colors.black,            fontWeight: FontWeight.bold          ),),            TextField(             autofocus: true,             textAlign: TextAlign.center,             onChanged: (newText){               newTaskTitle = newText;             },           ),          const SizedBox(height: 20),          TextButton(              onPressed: (){                Provider.of<TaskData>(context, listen: false).addTasks(newTaskTitle!);                Navigator.pop(context);              },          style: TextButton.styleFrom(            foregroundColor: Colors.white, backgroundColor: Colors.black,          ),            child: const Text(              'Add Task',style: TextStyle(            fontWeight: FontWeight.bold          ),),          )        ],      ),    );  }}